import { CommonModule } from '@angular/common';
import { Component, EventEmitter, Input, Output } from '@angular/core';
import { CoreComponent } from '../core/core.component';

@Component({
  selector: 'rjb-btn',
  standalone: true,
  imports: [CommonModule],
  template: `
    @if(block) {
      <div class="d-grid gap-2">
        <ng-container *ngTemplateOutlet="buttonTemplate"></ng-container>
      </div>
    } @else {
      <ng-container *ngTemplateOutlet="buttonTemplate"></ng-container>
    }
    <ng-template #buttonTemplate>
      <button [ngClass]="classes" (click)="onClick.emit($event)" [disabled]="disabled">
        <i *ngIf="icon" [ngClass]="onIcon"></i>
        {{ label }}
        <ng-content></ng-content>
      </button>
    </ng-template>
  `,
  styleUrls: ['./btn.component.css'],
})
export class BtnComponent extends CoreComponent {
  @Input() type: 'primary' | 'secondary' | 'success' | 'danger' = 'primary';
  @Input() outline = false;
  @Input() size: 'sm' | 'md' | 'lg' = 'md';
  @Input() label = '';
  /**
   * the icon is from the fontawesome lib and the format should be only the name part of the icon for example: fa fa-[icon-name]
   */
  @Input() icon = '';
  @Input() disabled = false;
  @Input() block = false;
  @Output() onClick = new EventEmitter<Event>();

  get classes(): string[] {
    const btnType = this.outline ? `btn-outline-${this.type}` : `btn-${this.type}`;
    const sizeClass = `btn-${this.size}`;
    return ['btn', btnType, sizeClass, this.disabled ? 'disabled' : ''].filter(Boolean);
  }

  get onIcon(): string {
    return `fa fa-${this.icon}`;
  }
}

import { Component, EventEmitter, Input, Output } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FontAwesomeModule } from '@fortawesome/angular-fontawesome';
import { faMoon, faSun } from '@fortawesome/free-solid-svg-icons';
// import { faMoon, faSun } from '@fortawesome/free-regular-svg-icons';


@Component({
  selector: 'rjb-btn-theme-mode',
  standalone: true,
  imports: [CommonModule, FontAwesomeModule],
  templateUrl: './btn-theme-mode.component.html',
  styleUrl: './btn-theme-mode.component.css',
})
export class BtnThemeModeComponent {
  faMoon = faMoon;
  faSun = faSun;
  /**
   * Is this the principal call to action on the page?
   */
  @Input()
  primary = false;

  /**
   * What background color to use
   */
  @Input()
  backgroundColor: 'primary' |  'secondary' |  'success' |  'info' |  'warning' |  'danger' |  'light' |  'dark' = 'light';

  /**
   * How large should the button be?
   */
  @Input()
  size: 'small' | 'medium' | 'large' = 'medium';

  /**
   * Button contents
   *
   * @required
   */
  @Input()
  label = 'Button';

  /**
   * Optional click handler
   */
  @Output()
  onClick = new EventEmitter<Event>();

  isDarkMode = false;
  themeClasses: string | undefined;

  public get classes(): string[] {
    const mode = this.primary
      ? 'storybook-button--primary'
      : 'storybook-button--secondary';

    return ['storybook-button', `storybook-button--${this.size}`, mode];
  }

  constructor() {
    const storedDarkMode = localStorage.getItem('color-theme');
    console.log('storedDarkMode', storedDarkMode);
    if (storedDarkMode !== null) {
      this.isDarkMode = storedDarkMode ? JSON.parse(storedDarkMode) : false;
    } else {
      const prefersDarkMode = window.matchMedia('(prefers-color-scheme: dark)').matches;
      console.log('prefersDarkMode', prefersDarkMode);
      this.isDarkMode = prefersDarkMode;
    }

    this.updateBodyClass();
  }

  ngOnInit(): void {
    this.updateBodyClass();
  }

  updateBodyClass() {
    document.documentElement.setAttribute('data-bs-theme', this.isDarkMode ? 'dark' : 'light');

    if (this.backgroundColor === 'dark' || this.backgroundColor === 'light') {
      this.backgroundColor = this.isDarkMode ? 'dark' : 'light';
    }
  }

  toggleDarkMode() {
    this.isDarkMode = !this.isDarkMode;
    localStorage.setItem('color-theme', JSON.stringify(this.isDarkMode));
    this.updateBodyClass();
  }
}

